import './App.css';

function App() {

    if (gemETCAskrate > gemBTCBidrate) {
        <p> hello</p>
    }else{
        <p>
            good bye
        </p>
    }

    return (
        <div className="Rates">

                <p id="gemETCBidrate"></p>

            <p id="gemETCAskrate">
                my exchange app
            </p>
            <p id="gemBTCBidrate">
                my exchange app
            </p>
            <p id="gemBTCAskrate">
                my exchange app
            </p>
            <p id="biETCBidrate">
                my exchange app
            </p>
            <p id="biETCAskrate">
                my exchange app
            </p>
            <p id="biBTCBidrate">
                my exchange app
            </p>
            <p id="biBTCAskrate">
                my exchange app
            </p>

        </div>
    );
}

axios.all([
    axios.get('https://api.gemini.com/v1/pubticker/ethusd'),
    axios.get('https://api.gemini.com/v1/pubticker/btcusd'),
    axios.get('https://api.binance.us/api/v3/ticker/24hr?symbol=ETHUSD'),
    axios.get('https://api.binance.us/api/v3/ticker/24hr?symbol=BTCUSD')
])
    .then(response => {
         document.getElementById("gemETCBidrate").innerHTML=response[0].data.bid;
        const gemETCAskrate = document.getElementById("gemETCAskrate").innerHTML=response[0].data.ask;
        const gemBTCBidrate = document.getElementById("gemBTCBidrate").innerHTML=response[1].data.bid;
        document.getElementById("gemBTCAskrate").innerHTML=response[1].data.ask;
        document.getElementById("biETCBidrate").innerHTML=response[2].data.bidPrice;
        document.getElementById("biETCAskrate").innerHTML=response[2].data.askPrice;
        document.getElementById("biBTCBidrate").innerHTML=response[3].data.bidPrice;
        document.getElementById("biBTCAskrate").innerHTML=response[3].data.askPrice;
        console.log('gemini ETH bid: $', response[0].data.bid);
        console.log('gemini ETH ask: $', response[0].data.ask);
        console.log('gemini BTC bid: $', response[1].data.bid);
        console.log('gemini BTC ask: $', response[1].data.ask);
        console.log('binance ETH bid: $', response[2].data.bidPrice);
        console.log('binance ETH ask: $', response[2].data.askPrice);
        console.log('binance BTC bid: $', response[3].data.bidPrice);
        console.log('binance BTC ask: $', response[3].data.askPrice);
    });

export default class CountryPage extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        HTTP.get('https://api.gemini.com/v1/pubticker/ethusd', (err, result) => {
            const money = result.ask;
            console.log('money', money);
            return (
                <div>
                    <Input type="select" name="dollars" id="crypto">
                        {money.map(money => (
                            <option>{money}</option>
                        ))}
                    </Input>
                </div>
            );
        });
    }
}

