import React, {Component, useEffect, useState} from 'react';
import axios from 'axios';
import logo from './logo.svg';
import './App.css';

function App() {
    return (
        <div className="App">
            <header className="App-header">
                <img src={logo} className="App-logo" alt="logo" />
                <p>
                    Edit <code>src/App.js</code> and save to reload.
                </p>
                <a
                    className="App-link"
                    href="https://reactjs.org"
                    target="_blank"
                    rel="noopener noreferrer"
                >
                    Learn React
                </a>




            </header>
        </div>
    );
}

axios.all([
    axios.get('https://api.gemini.com/v1/pubticker/ethusd'),
    axios.get('https://api.gemini.com/v1/pubticker/ethusd')
])
    .then(response => {
        console.log('ETH bid: ', response[0].data.bid);
        console.log('ETH ask: ', response[1].data.ask);
    });

useEffect(() => {
    //setAppState({ loading: true });
    const apiUrl = 'https://api.gemini.com/v1/pubticker/ethusd';
    axios.get(apiUrl).then((rates) => {
        const GemBuyEth = rates.ask;
        const GemSellEth = rates.bid;
        setAppState({ loading: false, GemBuyEth });
    });
}, //[setAppState]);
);


export default App;
