import React, {Component} from 'react';
import axios from 'axios';
import logo from './logo.svg';
import './App.css';

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>




      </header>
    </div>
  );
}

axios.all([
  axios.get('https://api.gemini.com/v1/pubticker/ethusd'),
  axios.get('https://api.gemini.com/v1/pubticker/ethusd')
])
    .then(response => {
      console.log('ETH bid: ', response[0].data.bid);
      console.log('ETH ask: ', response[1].data.ask);
    });

useEffect(() => {
    setAppState({ loading: true });
    const apiUrl = 'https://api.gemini.com/v1/pubticker/ethusd';
    axios.get(apiUrl).then((repos) => {
        const allRepos = repos.data;
        setAppState({ loading: false, repos: allRepos });
    });
}, [setAppState]);

class Data extends Component{

  state = {
    error: null,
    isLoaded: false,
    geminiBitBuy: ""
  };

  componentDidMount() {
    axios.get('https://api.gemini.com/v1/pubticker/ethusd').then(
        result => {
          this.setState({
            isLoaded: true,
            geminiBitBuy: result.data
          });
        },
        error => {
          this.setState({
            isLoaded: true,
            error
          });
        }
    );
  }
  render() {
    const { error, isLoaded, items } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
          <ul>
            {items.map(item => (
                <li key={item.username}>
                  {item.username}: {item.name}
                </li>
            ))}
          </ul>
      );
    }
  }
}
export default App;
